mapper.select:
select * from xl_order_send o 

	o.from_user=#{fromUser}

  order by o.time desc limit #{limitStart},#{limitLength}

select * from xl_order_send o 

	o.from_user=#{fromUser} and o.order_no=#{orderNo}

  order by o.time desc limit #{limitStart},#{limitLength}

select * from xl_order_send o

	o.to_user=#{toUser}

  order by o.time desc limit #{limitStart},#{limitLength}

select o.id oid, o.order_no, o.time, o.money, o.goodId, o.from_user, o.to_user,v.openId vopenId,v.nickName vnickName from xl_order_send o inner join xl_vip v on o.from_user=v.id

	o.to_user=#{toUser}

 order by o.time desc limit #{limitStart},#{limitLength}

select * from xl_order_send o

	o.to_user=#{toUser} and o.order_no=#{orderNo}

  order by o.time desc limit #{limitStart},#{limitLength}
